document.querySelector("#search_items_container");document.querySelector(".inventory_id").addEventListener("change",n=>{i(n.target.value)});function i(n){fetch(BASE_PATH+`/api/inventory/${n}`).then(e=>e.json()).then(e=>{e.item&&c(e.item.id,e.item)})}function c(n,e){document.querySelectorAll("input").forEach(t=>{t.name in e&&(t.value=e[t.name])}),document.querySelectorAll("textarea").forEach(t=>{t.name in e&&(t.value=e[t.name])})}document.addEventListener("change",n=>{const e=n.target;if(e.id=="quantity"){if(!e.value||e.value=="")return;const t=document.querySelector("#item_quantity").value;e.value>t&&(e.value=t)}n.stopPropagation()});
